data.value:
DECLARE @ProcessorNameString NVARCHAR(512) DECLARE @SystemManufacturer NVARCHAR(512) DECLARE @SystemProductName NVARCHAR(512) DECLARE @IsAgentRunning BIT DECLARE @InstantFileInitializationEnabled BIT IF OBJECT_ID('sys.xp_instance_regread') IS NOT NULL AND EXISTS(SELECT * FROM fn_my_permissions ( 'sys.xp_instance_regread', 'OBJECT' ) WHERE permission_name='EXECUTE') BEGIN EXEC sys.xp_instance_regread N'HKEY_LOCAL_MACHINE', N'HARDWARE\DESCRIPTION\System\CentralProcessor\0', N'ProcessorNameString',@ProcessorNameString OUT; EXEC sys.xp_instance_regread N'HKEY_LOCAL_MACHINE', N'SYSTEM\HardwareConfig\Current', N'SystemManufacturer',@SystemManufacturer OUT; EXEC sys.xp_instance_regread N'HKEY_LOCAL_MACHINE', N'SYSTEM\HardwareConfig\Current', N'SystemProductName', @SystemProductName OUT;
END DECLARE @ActivePowerPlan UNIQUEIDENTIFIER DECLARE @HighPerformance VARCHAR(36) DECLARE @Balanced VARCHAR(36) DECLARE @PowerSaver VARCHAR(36) SELECT @HighPerformance='8c5e7fda-e8bf-4a96-9a85-a6e23a8c635c',@Balanced='381b4222-f694-41f0-9685-ff5bb260df2e',@PowerSaver='a1841308-3541-4fab-bc81-f71556f20b4a' DECLARE @output TABLE( output NVARCHAR(MAX) ) IF EXISTS(SELECT * FROM sys.configurations WHERE name = 'xp_cmdshell' AND value_in_use=1 ) AND EXISTS(SELECT * FROM fn_my_permissions ( 'xp_cmdshell', 'OBJECT' ) WHERE permission_name='EXECUTE') BEGIN INSERT INTO @output ( output ) EXEC xp_cmdshell 'powercfg /list'; SELECT @ActivePowerPlan = CAST(MAX(CASE WHEN output LIKE '%' + @HighPerformance + '%*%' THEN @HighPerformance WHEN output LIKE '%' + @Balanced + '%*%' THEN @Balanced WHEN output LIKE '%' + @PowerSaver + '%*%' THEN @PowerSaver ELSE NULL END) AS UNIQUEIDENTIFIER) FROM @output
END ELSE BEGIN PRINT 'Enable xp_cmdshell to check power plan' END IF OBJECT_ID('sys.dm_server_services') IS NOT NULL BEGIN SELECT @IsAgentRunning = CASE WHEN status=4 THEN 1 ELSE 0 END FROM sys.dm_server_services dss WHERE dss.[servicename] LIKE N'SQL Server Agent (%'
IF COL_LENGTH('sys.dm_server_services','instant_file_initialization_enabled') IS NOT NULL BEGIN SELECT @InstantFileInitializationEnabled=CASE WHEN instant_file_initialization_enabled='Y' THEN 1 ELSE 0 END FROM sys.dm_server_services dss WHERE dss.[servicename] LIKE N'SQL Server (%'; END END DECLARE @OfflineSchedulers INT DECLARE @ResourceGovernorEnabled BIT SELECT @OfflineSchedulers=COUNT(*) FROM sys.dm_os_schedulers WHERE is_online=0 IF OBJECT_ID('sys.resource_governor_configuration') IS NOT NULL BEGIN SELECT @ResourceGovernorEnabled = CASE WHEN EXISTS(SELECT * FROM sys.resource_governor_configuration WHERE is_enabled=1) THEN CAST(1 AS BIT) ELSE CAST(0 AS BIT) END END DECLARE @WindowsRelease NVARCHAR(256) DECLARE @WindowsSP NVARCHAR(256) DECLARE @WindowsSKU INT IF OBJECT_ID('sys.dm_os_windows_info') IS NOT NULL BEGIN SELECT @WindowsRelease=windows_release,@WindowsSP = windows_service_pack_level,@WindowsSKU = windows_sku FROM sys.dm_os_windows_info END DECLARE @LastMemoryDump DATETIME DECLARE @DumpCount INT IF OBJECT_ID('sys.dm_server_memory_dumps') IS NOT NULL BEGIN select @LastMemoryDump=MAX(creation_time),@DumpCount=COUNT(*) from sys.dm_server_memory_dumps END SELECT @ActivePowerPlan ActivePowerPlanGUID, CASE WHEN @ActivePowerPlan = @HighPerformance THEN 'High Performance' WHEN @ActivePowerPlan = @Balanced THEN 'Balanced' WHEN @ActivePowerPlan = @PowerSaver THEN 'Power Saver' ELSE NULL END AS ActivePowerPlan, @ProcessorNameString AS ProcessorNameString, @SystemManufacturer AS SystemManufacturer, @SystemProductName AS SystemProductName, @IsAgentRunning IsAgentRunning, @InstantFileInitializationEnabled InstantFileInitializationEnabled, @OfflineSchedulers OfflineSchedulers, @ResourceGovernorEnabled AS ResourceGovernorEnabled, @WindowsRelease AS WindowsRelease, @WindowsSP AS WindowsServicePackLevel, @WindowsSKU AS WindowsSKU, @LastMemoryDump LastMemoryDump, @DumpCount AS MemoryDumpCount;

DECLARE @SQL NVARCHAR(MAX);
WITH cols AS ( SELECT 'softnuma_configuration' AS col,'INT' AS typ UNION ALL SELECT 'sql_memory_model' AS col,'INT' AS typ UNION ALL SELECT 'socket_count' AS col,'INT' AS typ UNION ALL SELECT 'cores_per_socket' AS col,'INT' AS typ UNION ALL SELECT 'numa_node_count' AS col,'INT' AS typ UNION ALL SELECT 'affinity_type' AS col,'INT' AS typ UNION ALL SELECT 'sqlserver_start_time' AS col,'DATETIME' AS typ UNION ALL SELECT 'os_priority_class' AS col,'INT' AS typ UNION ALL SELECT 'physical_memory_kb' AS col,'BIGINT' AS typ UNION ALL SELECT 'cpu_count' AS col,'INT' AS typ UNION ALL SELECT 'hyperthread_ratio' AS col,'INT' AS typ UNION ALL SELECT 'ms_ticks' AS col,'BIGINT' AS typ UNION ALL SELECT 'scheduler_count','INT' UNION ALL SELECT 'max_workers_count','INT' ) SELECT @SQL ='SELECT ' + STUFF((SELECT ',' + CASE WHEN col='physical_memory_kb' AND COLUMNPROPERTY(OBJECT_ID('sys.dm_os_sys_info'),'physical_memory_kb','ColumnID') IS NULL AND COLUMNPROPERTY(OBJECT_ID('sys.dm_os_sys_info'),'physical_memory_in_bytes','ColumnID') IS NOT NULL THEN 'physical_memory_in_bytes/1024 as physical_memory_kb' WHEN col='sqlserver_start_time' AND COLUMNPROPERTY(OBJECT_ID('sys.dm_os_sys_info'),col,'ColumnID') IS NULL THEN '(SELECT create_date FROM sys.databases WHERE name=''tempdb'') as sqlserver_start_time' WHEN COLUMNPROPERTY(OBJECT_ID('sys.dm_os_sys_info'),col,'ColumnID') IS NULL THEN 'CAST(NULL AS ' + typ + ') as ' + QUOTENAME(col) ELSE col END FROM cols ORDER BY col FOR XML PATH(''),TYPE).value('.','NVARCHAR(MAX)'),1,1,'') + ',DATEDIFF(mi,GETDATE(),GETUTCDATE()) AS UTCOffset FROM sys.dm_os_sys_info' PRINT @SQL
EXEC sp_executesql @SQL
